import React from 'react';
import { View, Image } from 'react-native';
import { Bubble, MessageImage } from 'react-native-gifted-chat';

const CustomMessage = (props) => {
  const { currentMessage } = props;
    console.log(currentMessage)
  // Check if the message has an image URL
  if (currentMessage.image) {
    return (
      <MessageImage
        {...props}
        imageStyle={{ borderRadius: 5 }} // Adjust the border radius as needed
      />
    );
  }

  return (
    <View style={{ flexDirection: 'row', alignItems: 'center' }}>
      {/* Display the sender's profile image */}
      <Image
        source={{ uri: currentMessage.user.avatar }}
        style={{ width: 30, height: 30, borderRadius: 15, marginRight: 8 }}
      />

      {/* Display the message bubble */}
      <Bubble {...props} />
    </View>
  );
};

export default CustomMessage;
