// ProfileScreen.js
import React, { useEffect } from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import ProfileBanner from './ProfileBanner';
import EventCard from './EventCard';
import { useAppContext } from './AppContext'; // Import the useAppContext hook
import userApi from '../api/userApi';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';

const ProfileScreen = () => {
  // Use the useAppContext hook to access the shared state
  const {
    user,
    setLoggedIn,
    myEvents,
    setMyEvents,
    myRsvps,
    setMyRsvps,
    setBrowseEvents,
    myInterested,
    setMyInterested,
  } = useAppContext();

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Use the user from context
        const eventsData = await userApi.getMyEvents(user._id);
        setMyEvents(eventsData || []); // Ensure eventsData is an array or default to an empty array
        const rsvpData = await userApi.getMyRsvps(user._id);
        setMyRsvps(rsvpData || []); // Ensure rsvpData is an array or default to an empty array
        const interestedData = await userApi.getMyInterested(user._id);
        setMyInterested(interestedData)
        console.log(interestedData)
      } catch (error) {
        console.error('Error fetching data:', error);
        // Handle the error as needed, e.g., show an error message to the user
      }
    };

    fetchData();
  }, [user._id, setMyEvents, setMyRsvps]);

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <ProfileBanner user={user} setLoggedIn={setLoggedIn} />

      <View style={styles.section}>
        <Text style={styles.label}>Hosting</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          {myEvents &&
            myEvents.map((event) => (
              <EventCard
                cardStyles={cardStyles}
                key={event._id}
                event={event}
                userId={user && user._id}
                setMyEvents={setMyEvents}
                setMyRsvps={setMyRsvps}
                setBrowseEvents={setBrowseEvents}
              />
            ))}
        </ScrollView>
      </View>

      <View style={styles.section}>
        <Text style={styles.label}>Attending</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          {myRsvps &&
            myRsvps.map((event) => (
              <EventCard
                cardStyles={cardStyles}
                key={event._id}
                event={event}
                userId={user && user._id}
                setMyEvents={setMyEvents}
                setMyRsvps={setMyRsvps}
                setBrowseEvents={setBrowseEvents}
              />
            ))}
        </ScrollView>
      </View>

      <View style={styles.section}>
        <Text style={styles.label}>Interested</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          {myInterested &&
            myInterested.map((event) => (
              <EventCard
                cardStyles={cardStyles}
                key={event._id}
                event={event}
                userId={user && user._id}
                setMyEvents={setMyEvents}
                setMyRsvps={setMyRsvps}
                setBrowseEvents={setBrowseEvents}
              />
            ))}
        </ScrollView>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingLeft: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingTop: 0,
    width: wp('100%'),
    alignItems: 'center',
  },
  section: {
    marginBottom: 20,
  },
  label: {
    alignSelf: 'flex-start',
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 10,
    color: 'black',
  },
  eventsContainer: {
    justifyContent: 'space-around',
    width: '100%',
    // Remove alignItems: 'center'
  },
  scrollView: {
    alignItems: 'center',
    width: ('100%')
  },
});

const cardStyles = StyleSheet.create({
  card: {
    backgroundColor: 'white',
    padding: 16,
    borderRadius: 8,
    margin: 8,
    elevation: 2,
    width: wp('50%'),
    alignSelf: 'center', // Align each card to the center horizontally
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 8,
  },
  icon: {
    width: 24,
    height: 24,
  },
  imageContainer: {
    height: hp('20%'), // Adjust the height as needed
    marginBottom: hp('12%'), // Add margin as needed
  },
  swiper: {
    height: hp('100%'), // Take the full height of the container
  },
  image: {
    height: '100%', // Take the full height of the Swiper
    width: wp('100%'), // Take the full width of the Swiper
    borderRadius: 8, // Adjust the border radius as needed
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 16,
    borderRadius: 8,
  },
  modalButtons: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 8,
  },
})

export default ProfileScreen;
