// ProfileScreen.js
import React, { useEffect, useState } from 'react';
import { View, StyleSheet, ScrollView } from 'react-native';
import ProfileBanner from './ProfileBanner';
import EventList from './EventList';
import { useAppContext } from './AppContext';
import userApi from '../api/userApi';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';
import DropDownPicker from 'react-native-dropdown-picker';

const ProfileScreen = () => {
  const {
    user,
    setLoggedIn,
    myEvents,
    setMyEvents,
    myRsvps,
    setMyRsvps,
    myInterested,
    setMyInterested,
  } = useAppContext();

  const [selectedSection, setSelectedSection] = useState('hosting');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const eventsData = await userApi.getMyEvents(user._id);
        setMyEvents(eventsData || []);
        const rsvpData = await userApi.getMyRsvps(user._id);
        setMyRsvps(rsvpData || []);
        const interestedData = await userApi.getMyInterested(user._id);
        setMyInterested(interestedData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [user._id, setMyEvents, setMyRsvps]);

  const handleSectionChange = (section) => {
    setSelectedSection(section);
  };

  return (
    <ScrollView style={styles.container}>
      <ProfileBanner user={user} setLoggedIn={setLoggedIn} />

      {/* Dropdown for Sections */}
      <DropDownPicker
        items={[
          { label: 'Hosting', value: 'hosting' },
          { label: 'Attending', value: 'attending' },
          { label: 'Interested', value: 'interested' },
        ]}
        defaultValue={selectedSection}
        containerStyle={styles.dropdownContainer}
        style={styles.dropdownStyle}
        itemStyle={styles.dropdownItemStyle}
        labelStyle={styles.dropdownLabelStyle}
        dropDownStyle={styles.dropdownDropStyle}
        onChangeItem={(item) => handleSectionChange(item.value)}
      />

      {/* Display EventList based on the selected section */}
      {selectedSection === 'hosting' && (
        <View style={styles.eventsContainer}>
          <EventList events={myEvents} cardStyles={cardStyles} title="Hosting" />
        </View>
      )}

      {selectedSection === 'attending' && (
        <View style={styles.eventsContainer}>
          <EventList events={myRsvps} cardStyles={cardStyles} title="Attending" />
        </View>
      )}

      {selectedSection === 'interested' && (
        <View style={styles.eventsContainer}>
          <EventList events={myInterested} cardStyles={cardStyles} title="Interested" />
        </View>
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginBottom: hp('1%'),
  },
  eventsContainer: {
    width: wp('90%'),
    marginLeft: wp('5%'),
    marginRight: wp('5%'),
  },
  dropdownContainer: {
    height: 40,
    marginVertical: 10,
  },
  dropdownStyle: {
    backgroundColor: '#fafafa',
  },
  dropdownItemStyle: {
    justifyContent: 'flex-start',
  },
  dropdownLabelStyle: {
    fontSize: 14,
    color: '#000',
  },
  dropdownDropStyle: {
    backgroundColor: '#fafafa',
  },
});

const cardStyles = StyleSheet.create({
  card: {
    backgroundColor: 'white',
    padding: 16,
    borderRadius: 8,
    margin: 8,
    elevation: 2,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 8,
  },
  swiper: {
    height: hp('100%'),
  },
  image: {
    height: '100%',
    width: wp('100%'),
    borderRadius: 8,
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 16,
    borderRadius: 8,
  },
  modalButtons: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 8,
  },
})

export default ProfileScreen;
