import React, { useEffect, useState } from 'react';
import { View, StyleSheet, ScrollView, Pressable, Text, FlatList } from 'react-native';
import ProfileBanner from './ProfileBanner';
import EventList from './EventList';
import { useAppContext } from './AppContext';
import userApi from '../api/userApi';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';

const ProfileScreen = () => {
  const {
    user,
    setLoggedIn,
    myEvents,
    setMyEvents,
    myRsvps,
    setMyRsvps,
    myInterested,
    setMyInterested,
  } = useAppContext();

  const [selectedSection, setSelectedSection] = useState('all'); // Set 'all' as the default tab

  useEffect(() => {
    const fetchData = async () => {
      try {
        const eventsData = await userApi.getMyEvents(user._id);
        setMyEvents(eventsData || []);
        const rsvpData = await userApi.getMyRsvps(user._id);
        setMyRsvps(rsvpData || []);
        const interestedData = await userApi.getMyInterested(user._id);
        setMyInterested(interestedData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [user._id, setMyEvents, setMyRsvps]);

  const handleSectionChange = (section) => {
    setSelectedSection(section);
  };

  const sections = [
    { key: 'all', title: 'All' },
    { key: 'hosting', title: 'Hosting' },
    { key: 'attending', title: 'Attending' },
    { key: 'interested', title: 'Interested' },
  ];

  const renderHeader = () => (
    <View style={styles.sectionToggleContainer}>
      {sections.map(item => (
        <Pressable
          key={item.key}
          onPress={() => handleSectionChange(item.key)}
          style={({ pressed }) => ({ opacity: pressed ? 0.5 : 1 })}
        >
          <Text style={selectedSection === item.key ? styles.selectedToggleText : styles.toggleText}>
            {item.title}
          </Text>
        </Pressable>
      ))}
    </View>
  );
  

  return (
    <View style={styles.container}>
      <ProfileBanner user={user} setLoggedIn={setLoggedIn} />

      <FlatList
        data={[]}
        ListHeaderComponent={renderHeader}
        renderItem={({ item }) => null}
        keyExtractor={() => 'dummyKey'} // Provide a dummy key to avoid key extraction warnings
      />

      {/* Display EventList based on the selected section */}
      <View style={styles.eventsContainer}>
        {/* New condition for 'All' tab */}
        {selectedSection === 'all' && (
          <EventList events={(myEvents || []).concat(myRsvps || [], myInterested || [])} title="All" />
        )}
        {selectedSection === 'hosting' && (
          <EventList events={(myEvents || []).filter(event => event.status !== 'cancelled')} title="Hosting" />
        )}
        {selectedSection === 'attending' && (
          <EventList events={(myRsvps || []).filter(event => event.status !== 'cancelled')} title="Attending" />
        )}
        {selectedSection === 'interested' && (
          <EventList events={(myInterested || []).filter(event => event.status !== 'cancelled')} title="Interested" />
        )}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginBottom: hp('1%'),
  },
  eventsContainer: {
    width: wp('90%'),
    marginLeft: wp('5%'),
    marginRight: wp('5%'),
  },
  sectionToggleContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginBottom: 10,
  },
  toggleText: {
    color: '#000',
    padding: 10,
  },
  selectedToggleText: {
    color: '#00f',
    padding: 10,
  },
});

export default ProfileScreen;
